(() => {
	"use strict";

	// Form Validation
	const forms = document.querySelectorAll(".needs-validation");

	Array.from(forms).forEach((form) => {
		console.log(form);
		form.addEventListener(
		"submit",
		(event) => {
			if (!form.checkValidity()) {
			event.preventDefault();
			event.stopPropagation();
			}

			form.classList.add("was-validated");
		},
		false
		);
	});

	// Date and Time Picker
	var $datepicker = $("#datepicker").datepicker();
	var $timepicker = $("#timepicker").timepicker({
		showMeridian: true,
		icons: {
			up: "fa fa-chevron-up",
			down: "fa fa-chevron-down",
		},
	});

	$("#calendar-icon").click(function () {
		$datepicker.datepicker("show");
	});

	$("#clock-icon").click(function () {
		$timepicker.timepicker("showWidget");
	});

	//Number Spinner
	$('.number-spinner').each(function() {
		var input = $(this);
		var incrementButton = input.closest('.input-group').find('.btn-outline-secondary:last');
		var decrementButton = input.closest('.input-group').find('.btn-outline-secondary:first');

		incrementButton.click(function() {
			var currentVal = parseInt(input.val());
			input.val(currentVal + 1);
		});

		decrementButton.click(function() {
			var currentVal = parseInt(input.val());
			if (currentVal > 0) {
				input.val(currentVal - 1);
			}
		});
	});
})();
